import React,{Component} from 'react';
import Lead from './lead';
import { Col } from 'reactstrap';
import { Nav } from 'reactstrap';
import { NavItem } from 'reactstrap';
import { NavLink } from 'reactstrap';
import { Row } from 'reactstrap';
import { TabContent } from 'reactstrap';
import { TabPane } from 'reactstrap';
class Leadads extends Component{
    
    state={
        leads:[],
        activeTab: new Array(6).fill('2'),
    }
    async componentDidMount() {

        const resto1 = await fetch("https://solutionsomg.com/webhoop/Leads/23844069292700195")
        const leads = await resto1.json();
        this.setState({ leads: leads })
    }
    toggle(tabPane, tab) {
        const newArray = this.state.activeTab.slice()
        newArray[tabPane] = tab
        this.setState({
            activeTab: newArray,
        });
    }// esta funcion activa las tabs por medio
    tabPane() {
        var {leads} = this.state;

        return (
           <div>
                <TabPane tabId="1" className="tabpan1">
              {

               leads.map(lead => (<Lead lead={lead}  key={lead.id}/> ))
              }
                </TabPane>
                <TabPane tabId="2" className="tabpan2">
                {

                    leads.map(lead => (<Lead lead={lead}  key={lead.id}/> ))
                   }
                 </TabPane>
               
            </div>
        );
    }

    leads(){

    }

    render(){


        <Row className="col-xl-12 col-md-12 col-sm-12 col-12 ml-auto mr-auto mt-1 row filtrodetabs p-0">
        <Col xs="12" md="12" className="mb-4">
            <Nav tabs className="col-12 flex-wrap d-flex">
            
                <NavItem className="col-6 col-sm-4 col-xl-3">
                <NavLink className="p-1"
                    active={this.state.activeTab[0] === '2'}
                    onClick={() => { this.toggle(0, '2');  this.setState({ iconos: 2 })}}
                >
                    Correctos
                </NavLink>
            </NavItem>
            <NavItem className="col-6 col-sm-4 col-xl-3">
            <NavLink className="p-1"
                active={this.state.activeTab[0] === '1'}
                onClick={() => { this.toggle(0, '1'); this.setState({ iconos: 1 })}}
            >
                Errores

            </NavLink>
        </NavItem>
            </Nav>
            <TabContent id="tabsdeprecios" activeTab={this.state.activeTab[0]}>
                {this.tabPane()}
            </TabContent>
        </Col>


    </Row>


    }

}
export default Leadads;
