            $(this).find("[data-form]").each(function () {
                if ($(this).val() !== "") filter += ("[" + $(this).attr("data-form") + "='" + $(this).val() + "']");
            });
            if (filter.length > 0) $("#datatotales .reporte").not(filter).hide();

            return false;
        })
