import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Error from './Error/Error';
import Reporte from './reporte/reportes';
import Nav from './nav';

import Header from './header';
import $ from 'jquery'

class Router extends Component {
     state = {
          productos : [],
          terminoBusqueda : '',
          usuario:[],
          log:[],
          usuarioactivado:0,
          loading:false,   usuarios: [
               {
                 "user": "edessociales.andrearendon@gmail.com",
                 "pass": "db3w2xjooxvp",
               },
               {
                 "user": "canalesespecializados@gmail.com",
                 "pass": "ylm5niqcr7cb"
         
               },
               {
                 "user": "andrea_rendon@bantrab.net.gt",
                 "pass": "r7cbdb3w2xss"
               }, {
         
                 "user": " maria_cermeno@bantrab.net.gt",
                 "pass": "zesiutllh8tg"
               }, {
         
                 "user": "  tatiana_meneses@bantrab.net.gt",
                 "pass": "1kfwvb3ex9pu"
               }, {
         
                 "user": " andrea_rendon@bantrab.net.gt",
                 "pass": "5z7zpkykt8jr"
               },
               {
                 "user": "admin",
                 "pass": "admin"
               }
             ]
     }

     async componentDidMount() {

          const resto = await fetch("https://solutionsomg.com/task/Token/api-flask-adops-*()")
        const token = await resto.json();
        this.setState({ token: token.token })
  


        }
        onSubmit = (e) => {
          e.preventDefault();      
          var {usuarios} = this.state;
          $.each(usuarios, function(i, v) {
          if (v.user === $("#usuario").val() && v.pass === $("#pass").val()) {
             $("#logueado").click();

          }else{
          $("input").addClass("is-invalid")
          }
          });
          
     }


     render() { 

          return ( 
               <BrowserRouter>
               <div className="App" >
               <Nav />
               <div className="d-flex flex-column col-11 p-0">
               <Header user={this.state.user} />         
                         <Switch>          
                              <Route exact path="/AdOps/ErroresPlataformas" render={() => (
                                   <Reporte  loading={this.state.loading} usuario={this.state.usuario} />)} />
                              <Route component={Error} />
                         </Switch>
                 </div>
                 </div>
               </BrowserRouter>
           )
     }
}
 
export default Router;
